package buoi3;

public class BinarySearch {
    /**
     * Tìm kiếm nhị phân trên mảng đã sắp xếp tăng dần.
     * @param arr mảng số nguyên đã sắp xếp
     * @param key giá trị cần tìm
     * @return chỉ số của phần tử nếu tìm thấy, -1 nếu không tìm thấy
     */
    public static int binarySearch(int[] arr, int key) {
        int left = 0;
        int right = arr.length - 1;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (arr[mid] == key) {
                return mid;
            } else if (arr[mid] < key) {
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }
        return -1;
    }
}

package buoi3;

public class BinarySearch {
    /**
     * Tìm kiếm nhị phân trên mảng đã sắp xếp tăng dần.
     * @param arr mảng số nguyên đã sắp xếp
     * @param key giá trị cần tìm
     * @return chỉ số của phần tử nếu tìm thấy, -1 nếu không tìm thấy
     */
    public static int binarySearch(int[] arr, int key) {
        int left = 0;
        int right = arr.length - 1;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (arr[mid] == key) {
                return mid;
            } else if (arr[mid] < key) {
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }
        return -1;
    }
}

package buoi2.btvn;

import java.util.Scanner;

/**
 * Bài tập 1: Quản lý Sinh viên
 * Yêu cầu:
 * - Tạo class SinhVien với các thuộc tính: maSV, hoTen, tuoi, diemTB
 * - Sử dụng constructor có tham số và không tham số
 * - Tạo getter/setter cho các thuộc tính private
 * - Tạo phương thức nhập thông tin sinh viên từ bàn phím
 * - Tạo phương thức hiển thị thông tin sinh viên
 * - Tạo phương thức xếp loại học lực (Giỏi >= 8, Khá >= 6.5, TB >= 5, Yếu < 5)
 */
public class SinhVien {

    // Biến static để đếm số lượng sinh viên
    public static int soLuongSinhVien = 0;

    // Các thuộc tính instance
    private String maSV;
    private String hoTen;
    private int tuoi;
    private double diemTB;

    // Constructor không tham số
    public SinhVien() {
        soLuongSinhVien++;
        System.out.println("Đã khởi tạo sinh viên thứ " + soLuongSinhVien);
    }

    // Constructor có tham số
    public SinhVien(String maSV, String hoTen, int tuoi, double diemTB) {
        this.maSV = maSV;
        this.hoTen = hoTen;
        this.tuoi = tuoi;
        this.diemTB = diemTB;
        soLuongSinhVien++;
    }

    // Phương thức nhập thông tin
    public void nhapThongTin() {
        Scanner sc = new Scanner(System.in);
        System.out.print("Nhập mã sinh viên: ");
        this.maSV = sc.nextLine();
        System.out.print("Nhập họ tên: ");
        this.hoTen = sc.nextLine();
        System.out.print("Nhập tuổi: ");
        this.tuoi = sc.nextInt();
        System.out.print("Nhập điểm trung bình: ");
        this.diemTB = sc.nextDouble();
    }

    // Phương thức hiển thị thông tin
    public void hienThiThongTin() {
        System.out.println("=== THÔNG TIN SINH VIÊN ===");
        System.out.println("Mã SV: " + this.maSV);
        System.out.println("Họ tên: " + this.hoTen);
        System.out.println("Tuổi: " + this.tuoi);
        System.out.println("Điểm TB: " + this.diemTB);
        System.out.println("Xếp loại: " + xepLoaiHocLuc());
    }

    // Phương thức xếp loại học lực
    public String xepLoaiHocLuc() {
        if (diemTB >= 8) {
            return "Giỏi";
        } else if (diemTB >= 6.5) {
            return "Khá";
        } else if (diemTB >= 5) {
            return "Trung bình";
        } else {
            return "Yếu";
        }
    }

    // Getter và Setter
    public String getMaSV() {
        return maSV;
    }

    public void setMaSV(String maSV) {
        this.maSV = maSV;
    }

    public String getHoTen() {
        return hoTen;
    }

    public void setHoTen(String hoTen) {
        this.hoTen = hoTen;
    }

    public int getTuoi() {
        return tuoi;
    }

    public void setTuoi(int tuoi) {
        this.tuoi = tuoi;
    }

    public double getDiemTB() {
        return diemTB;
    }

    public void setDiemTB(double diemTB) {
        this.diemTB = diemTB;
    }
}
