package buoi3;

public class BinarySearch {
    /**
     * Tìm kiếm nhị phân trên mảng đã sắp xếp tăng dần.
     * @param arr mảng số nguyên đã sắp xếp
     * @param key giá trị cần tìm
     * @return chỉ số của phần tử nếu tìm thấy, -1 nếu không tìm thấy
     */
    public static int binarySearch(int[] arr, int key) {
        int left = 0;
        int right = arr.length - 1;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (arr[mid] == key) {
                return mid;
            } else if (arr[mid] < key) {
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }
        return -1;
    }
}

package buoi3;

public class BinarySearch {
    /**
     * Tìm kiếm nhị phân trên mảng đã sắp xếp tăng dần.
     * @param arr mảng số nguyên đã sắp xếp
     * @param key giá trị cần tìm
     * @return chỉ số của phần tử nếu tìm thấy, -1 nếu không tìm thấy
     */
    public static int binarySearch(int[] arr, int key) {
        int left = 0;
        int right = arr.length - 1;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (arr[mid] == key) {
                return mid;
            } else if (arr[mid] < key) {
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }
        return -1;
    }
}

package buoi3;

import java.util.Arrays;

public class Main {
    public static void main(String[] args) {
        int[] arr = {1, 3, 5, 7, 9, 11, 15, 20};
        int key = 7;
        int result = BinarySearch.binarySearch(arr, key);

        System.out.println("Mảng: " + Arrays.toString(arr));
        System.out.println("Tìm kiếm giá trị " + key);
        if (result != -1) {
            System.out.println("Tìm thấy tại vị trí: " + result);
        } else {
            System.out.println("Không tìm thấy giá trị " + key);
        }
    }
}

